import React, { useEffect, useState } from 'react';
import {
  Box, Flex, Button, Modal, ModalBody, ModalCloseButton, ModalContent, ModalOverlay, Spinner, useToast,
} from "@chakra-ui/react";  // Import Chakra UI components and hooks
import { useDispatch, useSelector } from 'react-redux';  // Import Redux hooks
import { Link } from 'react-router-dom';
import { getItem } from '../../utility/localStorage';

const UserLogin = ({ handlechenge, Registarion, handleLogin, setRegistarion, handleLogout, admin }) => {
  const { isOpen, onOpen, onClose } = useDisclosure();  // UseDisclosure hook from Chakra UI
  const { isLoading, userData } = useSelector((store) => store.auth);  // useSelector hook from Redux
  const tocken = getItem("userData");
  const dispatch = useDispatch();
  const toast = useToast();
  
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  async function signUp() {
    let item = { name, password, email };

    try {
      let result = await fetch("http://localhost:8000/api/register", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Accept: 'application/json'
        },
        body: JSON.stringify(item)
      });
      result = await result.json();
      console.warn("result", result);
    } catch (error) {
      // Handle error
    }
  }

  useEffect(() => {
    // Handle authentication and registration responses
  }, [userData, dispatch]);

  return (
    <>
      {tocken === null ? (
        <Button
          onClick={onOpen}
          // Rest of your button styling
        >
          Login/Sign Up
        </Button>
      ) : (
        <Button
          onClick={handleLogout}
          // Rest of your button styling
        >
          Logout
        </Button>
      )}

      {!admin && (
        <Modal
          closeOnOverlayClick={false}
          isOpen={isOpen}
          onClose={onClose}
        >
          <ModalOverlay />
          <ModalContent>
            <ModalCloseButton />
            <ModalBody textAlign={"center"} pb={6}>
              {Registarion ? (
                <Flex flexDirection={"column"} gap={2}>
                  <h1>Registration</h1>
                  <input
                    type="text"
                    onChange={(e) => setName(e.target.value)}
                    placeholder="Name..."
                  />
                  <input
                    type="text"
                    onChange={(e) => setEmail(e.target.value)}
                    placeholder="Email..."
                  />
                  <input
                    type="password"
                    onChange={(e) => setPassword(e.target.value)}
                    placeholder="Password..."
                  />
                  <button onClick={signUp} className="btn btn-primary">
                    {isLoading ? <Spinner /> : "Submit"}
                  </button>
                  <Link onClick={() => setRegistarion(!Registarion)}>
                    Already have an account
                  </Link>
                </Flex>
              ) : (
                // Login form JSX
                <></>
              )}
            </ModalBody>
          </ModalContent>
        </Modal>
      )}
    </>
  );
};

export default UserLogin;
