import React from 'react';
import { ChakraProvider, Box, Grid, GridItem, Heading, Text, Radio, RadioGroup,Input, Button, IconButton, Container, Flex, FormControl, FormLabel, Image } from '@chakra-ui/react';
import { LockIcon, EmailIcon } from '@chakra-ui/icons';
import { FaSun, FaMoon, FaUndo } from "react-icons/fa";
import { Link } from 'react-router-dom';

const AdminLoginPage = () => {
  return (
    <ChakraProvider>
      <Box minHeight="100vh" bgGradient="gray">


      <Box py={4} color="black">
  <Container maxW="container.lg">
    <Flex alignItems="center" justifyContent="space-between">
    
        <Link href="/dashboard">
        <Heading size="lg" p={1} bg="beige" borderRadius="md" _hover={{ bg: "green.100", color: "black", boxShadow: "md" }}>
          Laksh
        </Heading>
      </Link>
    
      {/* Add additional interactive elements here */}
      <IconButton
        // icon={<FaUser />}
        aria-label="User Profile"
        variant="ghost"
        _hover={{ color: "Green.500" }}
      />
    </Flex>
  </Container>
</Box>



      
        {/* Header */}
        {/* <Box py={4} color="black">
          <Container maxW="container.lg">
            <Flex alignItems="center" justifyContent="space-between">
              {/* <Image src="/logo.svg" alt="Logo" boxSize="50px" /> */}
              {/* <Heading size="lg" p={1} bg="beige" >Laksh</Heading>
            </Flex>
          </Container>
        </Box> */} */}
        



        {/* Main Content */}
        <Flex>
          {/* Left Menu */}
          <GridItem colSpan={0} bg="gray" p={50} borderRadius="md" h={900}>


            <Heading size="md" mb={2}></Heading> {/* Increase mb value here */}
            <Box p={1}>
              <Box
      mb={10}
      bg="blue.100"
      borderRadius="md"
      width="100%"
      maxW="300px"
      _hover={{
        bg: "blue.200",
        cursor: "pointer",
        transform: "scale(1.05)",
        transition: "all 0.3s ease",
      }}
    >
      <Text p={4}>Dashboard</Text>
    </Box>
    <Box
      mb={10}
      bg="gray.200"
      borderRadius="md"
      width="100%"
      maxW="300px"
      _hover={{
        bg: "blue.200",
        cursor: "pointer",
        transform: "scale(1.05)",
        transition: "all 0.3s ease",
      }}
    >
      <Text p={4}>Seller Tool</Text>
    </Box>
    <Box
      mb={10}
      bg="blue.100"
      borderRadius="md"
      width="100%"
      maxW="300px"
      _hover={{
        bg: "blue.200",
        cursor: "pointer",
        transform: "scale(1.05)",
        transition: "all 0.3s ease",
      }}
    >
      <Text p={4}>Product</Text>
    </Box>

    <Box
      mb={10}
      bg="blue.100"
      borderRadius="md"
      width="100%"
      maxW="300px"
      _hover={{
        bg: "blue.200",
        cursor: "pointer",
        transform: "scale(1.05)",
        transition: "all 0.3s ease",
      }}
    >
      <Text p={4}>BuyLeads</Text>
    </Box>

            </Box>
          </GridItem>

      {/* ------CARDS--------------------------------------------------------------------------- */}
       
    <Container maxW="container.lg" py={3}>
      <Heading as="h1" mb={6} bg="gray" p={0}>.</Heading>
      
      {/* Cards Section */}
      <Flex flexWrap="wrap" justifyContent="space-between">
        {/* Card 1 */}
        <Box flex="0 0 23%" mb={4}>
  <Box
    bg="white"
    boxShadow="md"
    borderRadius="md"
    p={4}
    _hover={{
      bg: "teal.100", // Change background color on hover
      cursor: "pointer", // Change cursor on hover
      transform: "scale(1.05)", // Scale up on hover
      transition: "all 0.3s ease", // Add smooth transition
    }}
    onClick={() => {
      // Handle click event
      console.log("Card clicked");
    }}
  >
    <Heading as="h5" fontSize="sm" mb={2}>
      Category 1
    </Heading>
    <Heading as="h2" fontSize="xl" mb={2}>
      5
    </Heading>
    {/* Add additional content here, e.g., icons */}
    {/* <Icon as={FaChevronRight} boxSize={6} /> */}
  </Box>
</Box>


        {/* Card 2 */}
        <Box flex="0 0 23%" mb={4}>
  <Box
    bg="white"
    boxShadow="md"
    borderRadius="md"
    p={4}
    _hover={{
      bg: "teal.100", // Change background color on hover
      cursor: "pointer", // Change cursor on hover
      transform: "scale(1.05)", // Scale up on hover
      transition: "all 0.3s ease", // Add smooth transition
    }}
    onClick={() => {
      // Handle click event
      console.log("Card clicked");
    }}
  >
    <Heading as="h5" fontSize="sm" mb={2}>
      Category 2
    </Heading>
    <Heading as="h2" fontSize="xl" mb={2}>
      22
    </Heading>
    {/* Add additional content here, e.g., icons */}
    {/* <Icon as={FaChevronRight} boxSize={6} /> */}
  </Box>
</Box>


        {/* Card 3 */}
        <Box flex="0 0 23%" mb={4}>
  <Box
    bg="white"
    boxShadow="md"
    borderRadius="md"
    p={4}
    _hover={{
      bg: "teal.100", // Change background color on hover
      cursor: "pointer", // Change cursor on hover
      transform: "scale(1.05)", // Scale up on hover
      transition: "all 0.3s ease", // Add smooth transition
    }}
    onClick={() => {
      // Handle click event
      console.log("Card clicked");
    }}
  >
    <Heading as="h5" fontSize="sm" mb={2}>
      Category 3
    </Heading>
    <Heading as="h2" fontSize="xl" mb={2}>
      40
    </Heading>
    {/* Add additional content here, e.g., icons */}
    {/* <Icon as={FaChevronRight} boxSize={6} /> */}
  </Box>
</Box>


        {/* Card 4 */}
        <Box flex="0 0 23%" mb={4}>
  <Box
    bg="white"
    boxShadow="md"
    borderRadius="md"
    p={4}
    _hover={{
      bg: "teal.100", // Change background color on hover
      cursor: "pointer", // Change cursor on hover
      transform: "scale(1.05)", // Scale up on hover
      transition: "all 0.3s ease", // Add smooth transition
    }}
    onClick={() => {
      // Handle click event
      console.log("Card clicked");
    }}
  >
    <Heading as="h5" fontSize="sm" mb={2}>
      Category 4
    </Heading>
    <Heading as="h2" fontSize="xl" mb={2}>
      50
    </Heading>
    {/* Add additional content here, e.g., icons */}
    {/* <Icon as={FaChevronRight} boxSize={6} /> */}
  </Box>
</Box>

      </Flex>
    </Container>
        {/* --------------------------------------------------------------------------------- */}
        </Flex>

        {/* Footer */}
        <Box py={4} bg="gray" color="black" mt={4}>
          <Container maxW="container.lg">
            <Flex justifyContent="center">
              <Text>© 2024 Nixcel Software Pvt.Ltd All rights reserved.</Text>
            </Flex>
          </Container>
        </Box>
      </Box>
    </ChakraProvider>
  );
};

export default AdminLoginPage;
