import React, { useEffect, useState } from 'react';
import {
  Box, Flex, HStack, IconButton, useDisclosure, Stack, Input, Text, Image, Button, PopoverFooter, PopoverBody, PopoverHeader, PopoverCloseButton, PopoverArrow, PopoverContent, Popover, PopoverTrigger, Heading, Modal, ModalBody, ModalCloseButton, ModalContent, ModalFooter, ModalOverlay, Spinner, useToast,
} from "@chakra-ui/react";
import { useDispatch, useSelector } from 'react-redux';
import { Link } from 'react-router-dom';
import { getItem, setItem } from '../../utility/localStorage';
import { AddAdmin } from '../../redux/Admin/admin.action'; // Import AddAdmin action

const AdminLogin = ({ handlechenge, handleLogin, setRegistarion, cred, conform, handleLogout, admin }) => {
  const { isOpen, onOpen, onClose } = useDisclosure();
  const { isLoading, isError, adminData } = useSelector((store) => store.admin); // Update useSelector for admin state
  let token = getItem("adminData"); // Update storage key for admin token

  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const dispatch = useDispatch();
  const toast = useToast();

  const handleClick = async () => {
    let adminCred = { username, password };

    try {
      await dispatch(AddAdmin(adminCred)); // Dispatch AddAdmin action with admin credentials
    } catch (error) {
      console.error("Error occurred during admin login:", error);
      // Handle error as needed
    }
  };

  useEffect(() => {
    // Add any necessary useEffect logic for admin login
  }, [dispatch]);

  return (
    <>
      {token === null ? (
        <Button
          onClick={onOpen}
          fontSize={{ base: "12px", md: "14px" }}
          fontFamily="arial"
          background="-webkit-gradient(linear,left top,left bottom,from(#058b80),to(#02625a))"
          colorScheme="#fff"
          size="md"
          p="5px 40px"
        >
          Admin Login
        </Button>
      ) : (
        <Button
          onClick={handleLogout}
          fontSize={{ base: "12px", md: "14px" }}
          fontFamily="arial"
          background="-webkit-gradient(linear,left top,left bottom,from(#058b80),to(#02625a))"
          colorScheme="#fff"
          size="md"
          p="5px 40px"
        >
          Logout
        </Button>
      )}
      
      <Modal
        closeOnOverlayClick={false}
        isOpen={isOpen}
        onClose={onClose}
      >
        <ModalOverlay />
        <ModalContent>
          <ModalCloseButton />
          <ModalBody textAlign={"center"} pb={6}>
            <Flex mt={20} flexDirection={"column"} gap={2}>
              <Heading color={"#333"}>Admin Login</Heading>
              <Input
                placeholder="Username..."
                name="username"
                onChange={(e) => setUsername(e.target.value)}
              ></Input>
              <Input
                placeholder="Password..."
                type={"password"}
                name="password"
                onChange={(e) => setPassword(e.target.value)}
              ></Input>
              <Button
                onClick={handleClick}
                fontFamily="arial"
                background="-webkit-gradient(linear,left top,left bottom,from(#058b80),to(#02625a))"
                colorScheme="#fff"
                mr={3}
              >
                {isLoading ? <Spinner /> : "Login"}{" "}
              </Button>
            </Flex>
          </ModalBody>
        </ModalContent>
      </Modal>
    </>
  );
};

export default AdminLogin;
