} from "firebase/auth";

import {
  collection,
  addDoc,
  getDocs,
  doc,
  deleteDoc,
} from "firebase/firestore";
import axios from "axios";

const userRef = collection(db, "users");
const AdminRef = collection(db, "Admin");

export const AddUserApi = async (cred) => {
  try {
