export const setItem = (key, value) =>
  localStorage.setItem(key, JSON.stringify(value));

  function getItem() {
    // Assume data is retrieved somehow
    let jsonData = fetchData(); // Replace fetchData() with your actual data retrieval method
    if (jsonData) {
        try {
            let parsedData = JSON.parse(jsonData);
            return parsedData;
        } catch (error) {
            console.error('Error parsing JSON:', error);
            return null; // Or handle the error appropriately
        }
    } else {
        console.error('Data is undefined or null');
        return null; // Or handle the absence of data appropriately
    }
}


export const removeItem = (key) => localStorage.removeItem(key);

export const setItemSession = (key, value) =>
  sessionStorage.setItem(key, JSON.stringify(value));

export const getItemSession = (key) => JSON.parse(sessionStorage.getItem(key));

export const removeItemSession = (key) => sessionStorage.removeItem(key);
