  const [laststname, setLaststname] = useState("")
  const [email, setEmail] = useState("")
  const [password, setPassword] = useState("")
  const [conpassword, setconPassword] = useState("")
  const dispatch = useDispatch()
  const toast = useToast();

  const handleClick = async () => {
    if (password === conpassword) {
      let item = { firstname, laststname, email, password };
      console.warn(item);

      try {
        let result = await fetch("http://localhost:8000/api/register", {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
            Accept: 'application/json'
          },
          body: JSON.stringify(item)
        });
        result = await result.json();
        console.log("result", result);

        if (result.success) {
          // Handle successful signup
          console.log("User signed up successfully!");
          // You can dispatch an action here if needed
          dispatch(AddUser({ firstname, laststname, email, password }));
        } else {
          // Handle signup failure
          console.error("Signup failed:", result.error);
        }
      } catch (error) {
        console.error("Error occurred during signup:", error);
        // Handle other errors as needed
      }
    }
  }

 // const signin = getItem("signinuser")


