  return (
    <>

      {tocken === null ? (
        <Button
          onClick={onOpen}
          fontSize={{ base: "12px", md: "14px" }}
          fontFamily="arial"
          background="-webkit-gradient(linear,left top,left bottom,from(#058b80),to(#02625a))"
          colorScheme="#fff"
          size="md"
          p="5px 40px"
        >
          Login/signUp
        </Button>
      ) : (
        <Button
          onClick={handleLogout}
          fontSize={{ base: "12px", md: "14px" }}
          fontFamily="arial"
          background="-webkit-gradient(linear,left top,left bottom,from(#058b80),to(#02625a))"
          colorScheme="#fff"
          size="md"
          p="5px 40px"
        >
          Logout
        </Button>
      )}
      {!admin && (

        <Modal
          closeOnOverlayClick={false}
          isOpen={isOpen}
          onClose={onClose}
        >
          <ModalOverlay />
          <ModalContent>
            <ModalCloseButton />
            <ModalBody textAlign={"center"} pb={6}>
              {!Registarion && (
                <Flex mt={20} flexDirection={"column"} gap={2}>
                  <Heading color={"#333"}>Login</Heading>
